import { createContext, useState } from "react";
import axios from "axios";
import App from "../App";
export const CoursesContext = createContext();
const ContextProvider = () => {
    async function registerSubmit(ev, email, password, username) {
        ev.preventDefault();
        const data = await axios.post("http://localhost:4000/api/v1/user", {
            email,
            password,
            name: username,
        });
    }
    async function loginSubmit(ev, password, username) {
        ev.preventDefault();
        const data = await axios
            .post(
                "http://localhost:4000/api/v1/user",
                {
                    password,
                    name: username,
                },
                {
                    headers: {
                        token: "aplication/json",
                    },
                }
            )
            .then((response) => {
                console.log(response)
                localStorage.setItem("token", response);
            });
    }

    return (
        <CoursesContext.Provider value={{ registerSubmit, loginSubmit }}>
            <App />
        </CoursesContext.Provider>
    );
};

export default ContextProvider;
