import "../Styles/coursePreview.css";
import { useContext, useState, useEffect } from "react";
import { CoursesContext } from "../Helpers/Context";
const CoursePreview = () => {
    const { showAllSubjects } = useContext(CoursesContext);
    const [subjectList, setSubjectList] = useState([]);

    useEffect(() => {
        showAllSubjects();
    }, []);

    return (
        <>
            {subjectList.map}
            <div className="middleContainer">
                <div className="offsetContainer">
                    <img
                        src="https://www.startertutorials.com/blog/wp-content/uploads/2018/04/python-logo.png"
                        alt=""
                    />
                    <h1>Python</h1>
                    <h2>
                        Python is a high-level, interpreted programming language. It is known for its simple
                        syntax, code readability and ease of use, making it a popular choice for beginners and
                        experts alike. Python is used for a wide variety of tasks, including web development,
                        data analysis, artificial intelligence, and scientific computing. It is an open-source
                        language, which means that it is free to use and distribute.
                    </h2>
                    <button>Enroll Python course</button>
                </div>
            </div>
        </>
    );
};

export default CoursePreview;
