import "../Styles/coursePreview.css";
import { useContext, useState, useEffect } from "react";
import { CoursesContext } from "../Helpers/Context";
const CoursePreview = () => {
    const { showAllSubjects, subjects } = useContext(CoursesContext);

    useEffect(() => {
        showAllSubjects();
    }, []);
    return (
        <>
            <div className="subjectsContainer">
                {subjects.map((subject) => {
                    return (
                        <div id={subject.subject} className="middleContainer">
                            <div className="offsetContainer">
                                <div className="statsContainer">
                                    <img src={subject.image} alt="" />
                                    
                                </div>
                                <h1>{subject.subject}</h1>
                                <h2>{subject.description}</h2>
                                <button>Enroll {subject.subject} course</button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </>
    );
};

export default CoursePreview;
