import { createContext, useState } from "react";
import axios from "axios";
import App from "../App";
export const CoursesContext = createContext();
const ContextProvider = () => {
    const [currentPage, setCurrentPage] = useState("register");

    function checkIfLoggedIn(token) {
        token ? setCurrentPage("main") : setCurrentPage("register");
        console.log(token)
    }

    async function registerSubmit(ev, email, password, username) {
        ev.preventDefault();
        const data = await axios.post("http://localhost:4000/api/v1/user", {
            email,
            password,
            name: username,
        });
    }
    async function loginSubmit(ev, password, username) {
        ev.preventDefault();

        const login = await axios.post("http://localhost:4000/api/v1/user/login", {
            name: username,
            password: password,
        });
        localStorage.setItem("token", login.data);
        console.log(login);
    }

    return (
        <CoursesContext.Provider
            value={{ registerSubmit, loginSubmit, currentPage, setCurrentPage, checkIfLoggedIn }}
        >
            <App />
        </CoursesContext.Provider>
    );
};

export default ContextProvider;
