import { useContext, useEffect } from "react";
import Register from "./Components/register";
import Navbar from "./Components/Navbar";
import Login from "./Components/login";
import { CoursesContext } from "./Helpers/Context";
import Main from "./Components/main";

function App() {
    const { currentPage, setCurrentPage } = useContext(CoursesContext);

    function checkIfLoggedIn(token) {
        token ? setCurrentPage("main") : setCurrentPage("register");
    }
    useEffect(() => {
        checkIfLoggedIn(localStorage.getItem("token"));
    }, []);
    return (
        <>
            <Navbar />

            {currentPage === "register" ? <Register /> : ""}
            {currentPage === "login" ? <Login /> : ""}
            {currentPage === "main" ? <Main /> : ""}
        </>
    );
}

export default App;
