import { createContext, useState } from "react";
import axios from "axios";
import App from "../App";
export const CoursesContext = createContext();
const ContextProvider = () => {
    const [registerInformation, setRegisterInformation] = useState({});
    const secretTokenString = "secretToken";
    function registerSubmit(ev, username, password, email) {
        ev.preventDefault();
        setRegisterInformation({ name: username, password: password, email: email });
        registerPostFunction(registerInformation);
        console.log(username);
    }
    async function registerPostFunction(registerInformation) {
        const data = await axios.post("http://localhost:4000/api/v1/user", { registerInformation });
        console.log(data);
    }

    return (
        <CoursesContext.Provider value={{ setRegisterInformation, registerSubmit }}>
            <App />
        </CoursesContext.Provider>
    );
};

export default ContextProvider;
