                    </button>
                    <button onClick={() => setCurrentPage("personalArea")} className="registerButton">
                        Personal Area
                    </button>
                    <button onClick={() => logOut()} className="registerButton">
                        LogOut
                    </button>
                </div>
            </div>
        </>
    );
};

export default Navbar;
