import { createContext, useState } from "react";
import axios from "axios";
import App from "../App";
export const CoursesContext = createContext();
const ContextProvider = () => {
    const [currentPage, setCurrentPage] = useState("register");

    function checkIfLoggedIn() {
        const token = localStorage.getItem("token");
        console.log(token);
        token ? setCurrentPage("main") : setCurrentPage("login");
    }

    async function registerSubmit(ev, email, password, username) {
        ev.preventDefault();
        const data = await axios.post("http://localhost:4000/api/v1/user", {
            email,
            password,
            name: username,
        });
        localStorage.setItem("token", data.data);
        checkIfLoggedIn();
    }
    async function loginSubmit(ev, password, username) {
        ev.preventDefault();

        const login = await axios.post("http://localhost:4000/api/v1/user/login", {
            name: username,
            password: password,
        });
        localStorage.setItem("token", login.data);
        checkIfLoggedIn();
        console.log(login);
    }

    function logOut() {
        localStorage.removeItem("token");
        checkIfLoggedIn();
    }

    async function showAllCourses() {
        const courses = await axios.get("/api/v1/courses");
        
    }

    return (
        <CoursesContext.Provider
            value={{ registerSubmit, loginSubmit, currentPage, setCurrentPage, checkIfLoggedIn, logOut }}
        >
            <App />
        </CoursesContext.Provider>
    );
};

export default ContextProvider;
